int Ascii = 0;
int Begin2 = 1;
int Begin3 = 2;
int Begin4 = 3;
int Cont = 4;
int Invalid = 5;
int Start = 0;
int B2_1 = 1;
int B3_1 = 2;
int B4_1 = 3;
int B3_2 = 4;
int B4_2 = 5;
int B4_3 = 6;

bool EncodeRunes(BigStr* s, int bit8_display, mylib::BufWriter* buf) {
  bool valid_utf8;
  int state;
  BigStr* r1 = nullptr;
  BigStr* r2 = nullptr;
  BigStr* r3 = nullptr;
  int b;
  int typ;
  BigStr* out = nullptr;
  int rune;
  StackRoot _root0(&s);
  StackRoot _root1(&buf);
  StackRoot _root2(&r1);
  StackRoot _root3(&r2);
  StackRoot _root4(&r3);
  StackRoot _root5(&out);

  valid_utf8 = true;
  state = Start;
  r1 = str76;
  r2 = str77;
  r3 = str78;
  for (StrIter it(s); !it.Done(); it.Next()) {
    BigStr* byte = it.Value();
    StackRoot _for(&byte  );
    b = ord(byte);
    if (b < 127) {
      typ = Ascii;
    }
    else {
      if ((b >> 6) == 2) {
        typ = Cont;
      }
      else {
        if ((b >> 5) == 6) {
          typ = Begin2;
        }
        else {
          if ((b >> 4) == 14) {
            typ = Begin3;
          }
          else {
            if ((b >> 3) == 30) {
              typ = Begin4;
            }
            else {
              typ = Invalid;
            }
          }
        }
      }
    }
    if (typ != Cont) {
      if (state >= B2_1) {
        valid_utf8 = false;
        buf->write(XEscape(r1));
      }
      if (state >= B3_2) {
        buf->write(XEscape(r2));
      }
      if (state >= B4_3) {
        buf->write(XEscape(r3));
      }
    }
    if (typ == Ascii) {
      state = Start;
      if (str_equals(byte, str79)) {
        out = str80;
      }
      else {
        if (str_equals(byte, str81)) {
          out = str82;
        }
        else {
          if (str_equals(byte, str83)) {
            out = str84;
          }
          else {
            if (str_equals(byte, str85)) {
              out = str86;
            }
            else {
              if (str_equals(byte, str87)) {
                out = str88;
              }
              else {
                if (str_equals(byte, str89)) {
                  out = str90;
                }
                else {
                  if (IsUnprintableLow(byte)) {
                    if (bit8_display == BIT8_U_ESCAPE) {
                      out = UEscape(ord(byte));
                    }
                    else {
                      out = XEscape(byte);
                    }
                  }
                  else {
                    out = byte;
                  }
                }
              }
            }
          }
        }
      }
      buf->write(out);
    }
    else {
      if (typ == Begin2) {
        state = B2_1;
        r1 = byte;
      }
      else {
        if (typ == Begin3) {
          state = B3_1;
          r1 = byte;
        }
        else {
          if (typ == Begin4) {
            state = B4_1;
            r1 = byte;
          }
          else {
            if (typ == Invalid) {
              state = Start;
              buf->write(XEscape(byte));
              valid_utf8 = false;
            }
            else {
              if (typ == Cont) {
                if (state == Start) {
                  buf->write(XEscape(byte));
                  valid_utf8 = false;
                }
                else {
                  if (state == B2_1) {
                    if (bit8_display == BIT8_UTF8) {
                      out = str_concat(r1, byte);
                    }
                    else {
                      rune = (ord(byte) & 63);
                      rune |= ((ord(r1) & 31) << 6);
                      out = UEscape(rune);
                    }
                    buf->write(out);
                    state = Start;
                  }
                  else {
                    if (state == B3_1) {
                      r2 = byte;
                      state = B3_2;
                    }
                    else {
                      if (state == B3_2) {
                        if (bit8_display == BIT8_UTF8) {
                          out = str_concat(str_concat(r1, r2), byte);
                        }
                        else {
                          rune = (ord(byte) & 63);
                          rune |= ((ord(r2) & 63) << 6);
                          rune |= ((ord(r1) & 15) << 12);
                          out = UEscape(rune);
                        }
                        buf->write(out);
                        state = Start;
                      }
                      else {
                        if (state == B4_1) {
                          r2 = byte;
                          state = B4_2;
                        }
                        else {
                          if (state == B4_2) {
                            r3 = byte;
                            state = B4_3;
                          }
                          else {
                            if (state == B4_3) {
                              if (bit8_display == BIT8_UTF8) {
                                out = str_concat(str_concat(str_concat(r1, r2), r3), byte);
                              }
                              else {
                                rune = (ord(byte) & 63);
                                rune |= ((ord(r3) & 63) << 6);
                                rune |= ((ord(r2) & 63) << 12);
                                rune |= ((ord(r1) & 7) << 18);
                                out = UEscape(rune);
                              }
                              buf->write(out);
                              state = Start;
                            }
                            else {
                              assert(0);  // AssertionError
                            }
                          }
                        }
                      }
                    }
                  }
                }
              }
              else {
                assert(0);  // AssertionError
              }
            }
          }
        }
      }
    }
  }
  if (state >= B2_1) {
    valid_utf8 = false;
    buf->write(XEscape(r1));
  }
  if (state >= B3_2) {
    buf->write(XEscape(r2));
  }
  if (state >= B4_3) {
    buf->write(XEscape(r3));
  }
  return valid_utf8;
}

