mops::BigInt IntDivide(mops::BigInt x, mops::BigInt y) {
  mops::BigInt ZERO;
  int sign;
  mops::BigInt ax;
  mops::BigInt ay;
  ZERO = mops::BigInt(0);
  sign = 1;
  if (mops::Greater(ZERO, x)) {
    ax = mops::Negate(x);
    sign = -1;
  }
  else {
    ax = x;
  }
  if (mops::Greater(ZERO, y)) {
    ay = mops::Negate(y);
    sign = -sign;
  }
  else {
    ay = y;
  }
  return mops::Mul(mops::IntWiden(sign), mops::Div(ax, ay));
}

