  code_str = tup2.at0();
  code_loc = tup2.at1();
  Tuple2<BigStr*, syntax_asdl::loc_t*> tup3 = arg_r->ReadRequired2(str436);
  sig_spec = tup3.at0();
  sig_loc = tup3.at1();
