Tuple2<bool, mops::BigInt> _ParseOshInteger(BigStr* s, syntax_asdl::loc_t* blame_loc) {
  int id_;
  int pos;
  bool ok;
  mops::BigInt big_int;
  BigStr* b = nullptr;
  BigStr* digits = nullptr;
  int base;
  mops::BigInt integer;
  int digit;
  StackRoot _root0(&s);
  StackRoot _root1(&blame_loc);
  StackRoot _root2(&b);
  StackRoot _root3(&digits);

  Tuple2<int, int> tup0 = match::MatchShNumberToken(s, 0);
  id_ = tup0.at0();
  pos = tup0.at1();
  if (pos != len(s)) {
    return Tuple2<bool, mops::BigInt>(false, mops::BigInt(0));
  }
  if (id_ == Id::ShNumber_Dec) {
    Tuple2<bool, mops::BigInt> tup1 = mops::FromStr2(s);
    ok = tup1.at0();
    big_int = tup1.at1();
    if (!ok) {
      e_die(StrFormat("Integer too big: %s", s), blame_loc);
    }
    return Tuple2<bool, mops::BigInt>(true, big_int);
  }
  else {
    if (id_ == Id::ShNumber_Oct) {
      Tuple2<bool, mops::BigInt> tup2 = mops::FromStr2(s->slice(1), 8);
      ok = tup2.at0();
      big_int = tup2.at1();
      if (!ok) {
        e_die(StrFormat("Octal integer too big: %s", s), blame_loc);
      }
      return Tuple2<bool, mops::BigInt>(true, big_int);
    }
    else {
      if (id_ == Id::ShNumber_Hex) {
        Tuple2<bool, mops::BigInt> tup3 = mops::FromStr2(s->slice(2), 16);
        ok = tup3.at0();
        big_int = tup3.at1();
        if (!ok) {
          e_die(StrFormat("Hex integer too big: %s", s), blame_loc);
        }
        return Tuple2<bool, mops::BigInt>(true, big_int);
      }
      else {
        if (id_ == Id::ShNumber_BaseN) {
          Tuple2<BigStr*, BigStr*> tup4 = mylib::split_once(s, str1036);
          b = tup4.at0();
          digits = tup4.at1();
          try {
            base = to_int(b);
          }
          catch (ValueError*) {
            assert(0);  // AssertionError
          }
          if (base > 64) {
            e_strict(StrFormat("Base %d cannot be larger than 64", base), blame_loc);
          }
          if (base < 2) {
            e_strict(StrFormat("Base %d must be larger than 2", base), blame_loc);
          }
          integer = mops::ZERO;
          for (StrIter it(digits); !it.Done(); it.Next()) {
            BigStr* ch = it.Value();
            StackRoot _for(&ch          );
            if (IsLower(ch)) {
              digit = ((ord(ch) - ord(str40)) + 10);
            }
            else {
              if (IsUpper(ch)) {
                digit = ((ord(ch) - ord(str41)) + 36);
              }
              else {
                if (str_equals(ch, str1064)) {
                  digit = 62;
                }
                else {
                  if (str_equals(ch, str2)) {
                    digit = 63;
                  }
                  else {
                    if (ch->isdigit()) {
                      digit = to_int(ch);
                    }
                    else {
                      assert(0);  // AssertionError
                    }
                  }
                }
              }
            }
            if (digit >= base) {
              e_strict(StrFormat("Digits %r out of range for base %d", digits, base), blame_loc);
            }
            integer = mops::Add(mops::Mul(integer, mops::BigInt(base)), mops::BigInt(digit));
          }
          return Tuple2<bool, mops::BigInt>(true, integer);
        }
        else {
          return Tuple2<bool, mops::BigInt>(false, mops::BigInt(0));
        }
      }
    }
  }
}

